public static double findMedian(int[] arr) {
    double median;
    int n = arr.length;
    Arrays.sort(arr);

    if (n % 2 == 0)
        median = (arr[n / 2] + arr[(n / 2) - 1]) / 2.0;
    else
        median = arr[n / 2];

    return median;
}
