def alien_order(words):
    # Create a dictionary to store incoming and outgoing edges for each character
    incoming_edges = {char: 0 for word in words for char in word}
    outgoing_edges = {char: set() for word in words for char in word}

    # Add edges to the graph based on adjacent words
    for i in range(len(words) - 1):
        word1 = words[i]
        word2 = words[i + 1]

        if len(word1) > len(word2) and word1.startswith(word2):
            return ""

        for j in range(min(len(word1), len(word2))):
            if word1[j] != word2[j]:
                outgoing_edges[word1[j]].add(word2[j])
                incoming_edges[word2[j]] += 1
                break

    # Perform topological sort
    queue = [char for char in incoming_edges if incoming_edges[char] == 0]
    topo_order = ""

    while queue:
        char = queue.pop(0)
        topo_order += char

        for next_char in outgoing_edges[char]:
            incoming_edges[next_char] -= 1
            if incoming_edges[next_char] == 0:
                queue.append(next_char)

    if len(topo_order) < len(incoming_edges):
        return ""

    return topo_order
