			canvas.addEventListener('mousemove', function(evt) {
				var activeSegment = chart.getElementAtEvent(evt)[0];

				if(activeSegment) {
					for(var i=0; i<segments.length; i++) {
						segments[i]._model.backgroundColor = segments[i]._savedBackgroundColor;
