#include <iostream>
#include <mysql.h>

using namespace std;

void findHighestAvgSalaryPerEmployee(MYSQL* conn) {
    MYSQL_RES* res;
    MYSQL_ROW row;
    string query = "SELECT department_id, AVG(salary) as avg_salary_per_employee FROM employees GROUP BY department_id ORDER BY avg_salary_per_employee DESC LIMIT 1";

    if (mysql_query(conn, query.c_str())) {
        cerr << "Error querying database: " << mysql_error(conn) << endl;
        mysql_close(conn);
        exit(1);
    }

    res = mysql_use_result(conn);

    if (res) {
        row = mysql_fetch_row(res);

        cout << "Department with highest average salary per employee: " << row[0] << endl;
    } else {
        cerr << "Error getting result set: " << mysql_error(conn) << endl;
        mysql_close(conn);
        exit(1);
    }

    mysql_free_result(res);
}

