    MYSQL* conn = mysql_init(NULL);

    if (conn == NULL) {
        cerr << "Error initializing MySQL: " << mysql_error(conn) << endl;
        exit(1);
    }

    if (mysql_real_connect(conn, "localhost", "username", "password", "database", 3306, NULL, 0) == NULL) {
        cerr << "Error connecting to database: " << mysql_error(conn) << endl;
