#include <iostream>
#include <vector>

using namespace std;

void findCombinations(int n, int k, vector<int>& current, vector<vector<int>>& result) {
    if (k == 0) {
        if (n == 0) {
            result.push_back(current);
        }
        return;
    }
    for (int i = 1; i <= 9; i++) {
        if (n - i >= 0) {
            current.push_back(i);
            findCombinations(n - i, k - 1, current, result);
            current.pop_back();
        }
    }
}

int main() {
    int n, k;
    cout << "Enter the value of n: ";
    cin >> n;
    cout << "Enter the value of k: ";
    cin >> k;
    vector<vector<int>> result;
    vector<int> current;
    findCombinations(n, k, current, result);
    for (auto combination : result) {
        for (int i = 0; i < combination.size(); i++) {
            cout << combination[i];
            if (i != combination.size() - 1) {
                cout << " + ";
            }
        }
        cout << endl;
    }
    return 0;
}
