#include <iostream>
#include <vector>

enum class Order {
    ascending,
    descending,
    none
};

Order checkOrder(const std::vector<int>& list) {
    bool is_ascending = true;
    bool is_descending = true;

    for (size_t i = 1; i < list.size(); ++i) {
        if (list[i] > list[i-1]) {
            is_descending = false;
        } else if (list[i] < list[i-1]) {
            is_ascending = false;
        }
    }

    if (is_ascending) {
        return Order::ascending;
    } else if (is_descending) {
        return Order::descending;
    } else {
        return Order::none;
    }
}

int main() {
    std::vector<int> list_ascending = {1, 2, 3, 4, 5};
    std::vector<int> list_descending = {5, 4, 3, 2, 1};
    std::vector<int> list_none = {1, 3, 2, 5, 4};

    std::cout << "List (ascending): "
              << (checkOrder(list_ascending) == Order::ascending ? "Sorted in ascending order" : "Not sorted in ascending order")
              << std::endl;
    std::cout << "List (descending): "
              << (checkOrder(list_descending) == Order::descending ? "Sorted in descending order" : "Not sorted in descending order")
              << std::endl;
    std::cout << "List (none): "
              << (checkOrder(list_none) == Order::none ? "Not sorted" : "Sorted")
              << std::endl;

    return 0;
}
