function findSecondHighest(arr){
  //remove duplicates from array
  let uniqueArray = [...new Set(arr)];

  //sort the array in descending order
  uniqueArray.sort(function(a, b) {
    return b - a;
  });

  // return second element
  return uniqueArray[1];
}

