def sort_numbers(arr):
    for i in range(len(arr)): 
        for j in range(i): 
            if arr[i] < arr[j]: 
                arr[i], arr[j] = arr[j], arr[i]
    return arr

def linearSearch(arr, n, x): 
    for i in range (0, n): 
        if (arr[i] == x): 
            return i 
    return -1
