def summaryRanges(nums):
    n = len(nums)
    if n == 0:
        return []
    res = []
    start = nums[0]
    for i in range(1, n):
        if nums[i] == nums[i-1] + 1:
            continue
        else:
            if start == nums[i-1]:
                res.append(str(start))
            else:
                res.append(str(start) + '->' + str(nums[i-1]))
            start = nums[i]
    if start == nums[n-1]:
        res.append(str(start))
    else:
        res.append(str(start) + '->' + str(nums[n-1]))
    return res
