#include <vector>
#include <iostream>

using namespace std;

const int MAX_CHAR = 26;

struct TrieNode {
    vector<TrieNode*> children;
    bool isEnd;

    TrieNode() : children(MAX_CHAR, nullptr), isEnd(false) {}
};

class Trie {
public:
    Trie() {
        root = new TrieNode();
    }

    void insert(string word) {
        TrieNode* currNode = root;
        for (char c : word) {
            int index = c - 'a';
            if (currNode->children[index] == nullptr) {
                currNode->children[index] = new TrieNode();
            }
            currNode = currNode->children[index];
        }
        currNode->isEnd = true;
    }

    bool search(string word) {
        TrieNode* currNode = root;
        for (char c : word) {
            int index = c - 'a';
            if (currNode->children[index] == nullptr) {
                return false;
            }
            currNode = currNode->children[index];
        }
        return currNode->isEnd;
    }

    bool startsWith(string prefix) {
        TrieNode* currNode = root;
        for (char c : prefix) {
            int index = c - 'a';
            if (currNode->children[index] == nullptr) {
                return false;
            }
            currNode = currNode->children[index];
        }
        return true;
    }

private:
    TrieNode* root;
};
