def validate_octet(s):
    """Function to check for the wildcard"""
    if s == "*":
        return True
    if s == "-":
        return True
    if not s.isdigit() or int(s) > 255:
        return False
    return True

def validate_IPv4(s): 
    """Function to validate a given IPv4 address"""
    l = s.split(".")

